          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
            {Array.from({ length: 12 }).map((_, index) => (
              <div key={index} className="bg-card rounded-xl border border-border overflow-hidden">
                <Skeleton className="w-full h-48" />
                <div className="p-6">
                  <Skeleton className="h-6 w-3/4 mb-2" />
                  <Skeleton className="h-4 w-full mb-3" />
                  <div className="flex items-center justify-between">
                    <Skeleton className="h-4 w-24" />
                    <Skeleton className="h-4 w-20" />
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>
    );
  }

  return (
    <section id="projects" className="py-20 bg-background">
      <div className="container mx-auto px-4">
        <div className="text-center mb-16">
          <h3 className="text-4xl font-bold text-foreground mb-4">Our Projects Portfolio</h3>
          <p className="text-xl text-muted-foreground max-w-2xl mx-auto">
            Explore our comprehensive collection of construction, renovation, and specialized building projects across Cape Town and beyond.
          </p>
        </div>

        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8 mb-12">
          {projects?.slice(0, visibleCount).map((project) => (
            <div 
              key={project.id} 
              className="bg-card rounded-xl border border-border overflow-hidden hover:border-orange-primary/50 transition-all"
              data-testid={`project-card-${project.id}`}
            >
              <img 
                src={project.imageUrl} 
                alt={project.title} 
                className="w-full h-48 object-cover"
              />
              <div className="p-6">
                <h4 className="text-lg font-semibold text-foreground mb-2">{project.title}</h4>
                <p className="text-muted-foreground text-sm mb-3">{project.description}</p>
                <div className="flex items-center justify-between">
                  <span className="text-orange-primary font-medium">
                    {t('projects.year')}: {project.completedYear}
                  </span>
                  <ProjectDetailsModal project={project}>
                    <button 
                      className="text-orange-primary hover:text-orange-secondary text-sm font-medium transition-colors"
                      data-testid={`project-details-${project.id}`}
                    >
                      {t('projects.viewDetails')}
                    </button>
                  </ProjectDetailsModal>
                </div>
              </div>
            </div>
          ))}
        </div>

        {hasMoreProjects && (
          <div className="text-center">
            <button 
              onClick={loadMore}
              className="bg-orange-primary hover:bg-orange-secondary text-white px-8 py-3 rounded-lg transition-colors font-medium"
              data-testid="see-more-projects"
            >
              {t('common.view')} {t('nav.projects')}
            </button>
          </div>
        )}
      </div>
    </section>
  );
}

client/src/components/quote-form.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useTranslation } from "react-i18next";
import { insertQuoteRequestSchema } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import type { InsertQuoteRequest } from "@shared/schema";

export default function QuoteForm() {
  const { t } = useTranslation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [showCustomBudget, setShowCustomBudget] = useState(false);

  const form = useForm<InsertQuoteRequest>({
    resolver: zodResolver(insertQuoteRequestSchema),
    defaultValues: {
      fullName: "",
      phone: "",
      email: "",
      projectType: "",
      budget: "",
      location: "",
      description: "",
    },
  });

  const submitQuoteMutation = useMutation({
    mutationFn: async (data: InsertQuoteRequest) => {
      const response = await apiRequest("POST", "/api/quote-requests", data);
      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: t('quote.toast.success'),
        description: data.message,
      });
      form.reset();
      queryClient.invalidateQueries({ queryKey: ["/api/quote-requests"] });
    },
    onError: (error: any) => {
      toast({
        title: t('common.error'),
        description: error.message || t('quote.toast.error'),
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: InsertQuoteRequest) => {
    submitQuoteMutation.mutate(data);
  };

  return (
    <section id="quote" className="py-20 bg-blue-primary">
      <div className="container mx-auto px-4">
        <div className="max-w-4xl mx-auto">
          <div className="text-center text-white mb-12">
            <h3 className="text-4xl font-bold mb-4">{t('quote.title')}</h3>
            <p className="text-xl text-blue-100">
              {t('quote.subtitle')}
            </p>
          </div>

          <div className="bg-white rounded-xl shadow-2xl p-8">
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="grid md:grid-cols-2 gap-6">
                <FormField
                  control={form.control}
                  name="fullName"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>{t('quote.form.fullName')}</FormLabel>
                      <FormControl>
                        <Input 
                          placeholder={t('quote.form.fullNamePlaceholder')} 
                          {...field} 
                          data-testid="input-full-name"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="phone"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>{t('quote.form.phoneNumber')}</FormLabel>
                      <FormControl>
                        <Input 
                          placeholder={t('quote.form.phoneNumberPlaceholder')} 
                          {...field} 
                          data-testid="input-phone"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="email"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>{t('quote.form.emailAddress')}</FormLabel>
                      <FormControl>
                        <Input 
                          type="email" 
                          placeholder={t('quote.form.emailAddressPlaceholder')} 
                          {...field} 
                          data-testid="input-email"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="projectType"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>{t('quote.form.projectType')}</FormLabel>
                      <Select onValueChange={field.onChange} defaultValue={field.value}>
                        <FormControl>
                          <SelectTrigger data-testid="select-project-type">
                            <SelectValue placeholder={t('quote.form.selectProjectType')} />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="new-construction">{t('quote.form.projectTypes.newConstruction')}</SelectItem>
                          <SelectItem value="renovation">{t('quote.form.projectTypes.renovation')}</SelectItem>
                          <SelectItem value="interior-exterior">{t('quote.form.projectTypes.interiorExterior')}</SelectItem>
                          <SelectItem value="maintenance">{t('quote.form.projectTypes.maintenance')}</SelectItem>
                          <SelectItem value="commercial">{t('quote.form.projectTypes.commercial')}</SelectItem>
                          <SelectItem value="other">{t('quote.form.projectTypes.other')}</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="budget"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>{t('quote.form.budgetRange')}</FormLabel>
                      <Select 
                        onValueChange={(value) => {
                          field.onChange(value);
                          setShowCustomBudget(value === "custom");
                        }} 
                        defaultValue={field.value || ""}
                      >
                        <FormControl>
                          <SelectTrigger data-testid="select-budget">
                            <SelectValue placeholder={t('quote.form.selectBudgetRange')} />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="under-5k">{t('quote.form.budgetRanges.under5k')}</SelectItem>
                          <SelectItem value="5k-15k">{t('quote.form.budgetRanges.5k15k')}</SelectItem>
                          <SelectItem value="15k-30k">{t('quote.form.budgetRanges.15k30k')}</SelectItem>
                          <SelectItem value="30k-50k">{t('quote.form.budgetRanges.30k50k')}</SelectItem>
                          <SelectItem value="50k-100k">{t('quote.form.budgetRanges.50k100k')}</SelectItem>
                          <SelectItem value="over-100k">{t('quote.form.budgetRanges.over100k')}</SelectItem>
                          <SelectItem value="custom">{t('quote.form.budgetRanges.custom')}</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {showCustomBudget && (
                  <div className="space-y-2">
                    <label htmlFor="customBudget" className="text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70">
                      {t('quote.form.enterBudget')}
                    </label>
                    <Input
                      id="customBudget"
                      type="number"
                      placeholder={t('quote.form.enterAmountPlaceholder')}
                      data-testid="input-custom-budget"
                      className="mt-1"
                    />
                  </div>
                )}

                <FormField
                  control={form.control}
                  name="location"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>{t('quote.form.location')}</FormLabel>
                      <FormControl>
                        <Input 
                          placeholder={t('quote.form.locationPlaceholder')} 
                          {...field} 
                          data-testid="input-location"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="md:col-span-2">
                  <FormField
                    control={form.control}
                    name="description"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>{t('quote.form.projectDescription')}</FormLabel>
                        <FormControl>
                          <Textarea 
                            rows={4} 
                            placeholder={t('quote.form.projectDescriptionPlaceholder')} 
                            className="resize-none" 
                            {...field} 
                            data-testid="textarea-description"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <div className="md:col-span-2">
                  <Button 
                    type="submit" 
                    className="w-full bg-orange-primary hover:bg-orange-secondary text-white py-4 text-lg font-semibold"
                    disabled={submitQuoteMutation.isPending}
                    data-testid="button-submit-quote"
                  >
                    {submitQuoteMutation.isPending ? t('common.submitting') : t('quote.form.submitButton')}
                  </Button>
                </div>
              </form>
            </Form>
          </div>
        </div>
      </div>
    </section>
  );
}

client/src/components/review-display.tsx.content:
import { useQuery } from "@tanstack/react-query";
import type { Review } from "@shared/schema";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Star, MapPin, ThumbsUp, Calendar } from "lucide-react";

export function ReviewDisplay() {
  const { data: reviews, isLoading } = useQuery<Review[]>({
    queryKey: ["/api/reviews/approved"],
  });

  const renderStars = (rating: string) => {
    const numRating = parseInt(rating);
    return (
      <div className="flex gap-1" data-testid={`stars-${rating}`}>
        {[1, 2, 3, 4, 5].map((star) => (
          <Star
            key={star}
            className={`w-4 h-4 ${
              star <= numRating 
                ? "text-yellow-400 fill-current" 
                : "text-gray-300 dark:text-gray-600"
            }`}
          />
        ))}
      </div>
    );
  };

  const getServiceBadgeColor = (service: string) => {
    const colors: { [key: string]: string } = {
      brickwork: "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200",
      plumbing: "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200",
      carpentry: "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200",
      plastering: "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200",
      tiling: "bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200",
      landscaping: "bg-emerald-100 text-emerald-800 dark:bg-emerald-900 dark:text-emerald-200",
      painting: "bg-indigo-100 text-indigo-800 dark:bg-indigo-900 dark:text-indigo-200"
    };
    return colors[service] || "bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-200";
  };

  if (isLoading) {
    return (
      <div className="space-y-6" data-testid="reviews-loading">
        {[1, 2, 3].map((i) => (
          <Card key={i} className="animate-pulse">
            <CardHeader>
              <div className="flex items-center justify-between">
                <div className="space-y-2">
                  <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-32"></div>
                  <div className="h-3 bg-gray-200 dark:bg-gray-700 rounded w-20"></div>
                </div>
                <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-16"></div>
              </div>
            </CardHeader>
            <CardContent>
              <div className="space-y-2">
                <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-full"></div>
                <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-3/4"></div>
                <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-1/2"></div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    );
  }

  if (!reviews || reviews.length === 0) {
    return (
      <Card className="text-center py-12" data-testid="no-reviews">
        <CardContent>
          <div className="text-gray-500 dark:text-gray-400">
            <Star className="w-12 h-12 mx-auto mb-4" />
            <h3 className="text-lg font-semibold mb-2">No Reviews Yet</h3>
            <p>Be the first to share your experience with Kanguya Builders!</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  const averageRating = reviews.reduce((acc, review) => acc + parseInt(review.rating), 0) / reviews.length;

  return (
    <div className="space-y-6">
      {/* Overall Rating Summary */}
      <Card className="bg-gradient-to-r from-orange-50 to-blue-50 dark:from-orange-900/20 dark:to-blue-900/20">
        <CardContent className="pt-6">
          <div className="text-center">
            <div className="text-4xl font-bold text-orange-600 dark:text-orange-400 mb-2">
              {averageRating.toFixed(1)}
            </div>
            <div className="flex justify-center mb-2" data-testid="average-rating-stars">
              {renderStars(Math.round(averageRating).toString())}
            </div>
            <p className="text-gray-600 dark:text-gray-400">
              Based on {reviews.length} customer review{reviews.length !== 1 ? 's' : ''}
            </p>
          </div>
        </CardContent>
      </Card>

      {/* Individual Reviews */}
      <div className="space-y-4" data-testid="reviews-list">
        {reviews.map((review, index) => (
          <Card key={review.id} className="hover:shadow-lg transition-shadow" data-testid={`review-${index}`}>
            <CardHeader className="pb-3">
              <div className="flex items-start justify-between">
                <div className="space-y-1">
                  <h4 className="font-semibold text-lg text-blue-900 dark:text-blue-100">
                    {review.title}
                  </h4>
                  <div className="flex items-center gap-2">
                    <span className="font-medium text-gray-900 dark:text-gray-100">
                      {review.customerName}
                    </span>
                    {review.projectLocation && (
                      <div className="flex items-center gap-1 text-sm text-gray-600 dark:text-gray-400">
                        <MapPin className="w-3 h-3" />
                        {review.projectLocation}
                      </div>
                    )}
                  </div>
                </div>
                <div className="text-right space-y-2">
                  {renderStars(review.rating)}
                  <div className="text-xs text-gray-500 dark:text-gray-400 flex items-center gap-1">
                    <Calendar className="w-3 h-3" />
                    {review.createdAt ? new Date(review.createdAt).toLocaleDateString() : 'Recently'}
                  </div>
                </div>
              </div>
            </CardHeader>
            <CardContent>
              <p className="text-gray-700 dark:text-gray-300 mb-4 leading-relaxed">
                {review.review}
              </p>

              <div className="flex flex-wrap items-center gap-2 mb-3">
                <Badge className={getServiceBadgeColor(review.serviceUsed)}>
                  {review.serviceUsed}
                </Badge>

                {review.recommendToOthers === "yes" && (
                  <Badge variant="outline" className="text-green-700 border-green-300 dark:text-green-400 dark:border-green-600">
                    <ThumbsUp className="w-3 h-3 mr-1" />
                    Recommends
                  </Badge>
                )}
              </div>

              {review.recommendToOthers === "yes" && (
                <div className="text-sm text-green-700 dark:text-green-400 font-medium">
                  âœ“ Would recommend Kanguya Builders to others
                </div>
              )}
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
}

client/src/components/review-form.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useTranslation } from "react-i18next";
import { z } from "zod";
import { insertReviewSchema } from "@shared/schema";
import { Button } from "@/components/ui/button";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Star, Send, CheckCircle } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

const reviewFormSchema = insertReviewSchema.extend({
  rating: z.string().min(1, "Please select a rating"),
  title: z.string().min(5, "Title must be at least 5 characters"),
  review: z.string().min(20, "Review must be at least 20 characters"),
  customerName: z.string().min(2, "Name must be at least 2 characters"),
  customerEmail: z.string().email("Please enter a valid email address"),
  serviceUsed: z.string().min(1, "Please select the service used"),
  recommendToOthers: z.string().min(1, "Please select if you would recommend us")
});

type ReviewFormData = z.infer<typeof reviewFormSchema>;

interface ReviewFormProps {
  onSuccess?: () => void;
}

export function ReviewForm({ onSuccess }: ReviewFormProps) {
  const { t } = useTranslation();
  const [isSubmitted, setIsSubmitted] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<ReviewFormData>({
    resolver: zodResolver(reviewFormSchema),
    defaultValues: {
      customerName: "",
      customerEmail: "",
      customerPhone: "",
      rating: "",
      title: "",
      review: "",
      serviceUsed: "",
      projectLocation: "",
      recommendToOthers: ""
    },
  });

  const createReview = useMutation({
    mutationFn: async (data: ReviewFormData) => {
      const response = await apiRequest("POST", "/api/reviews", data);
      return response.json();
    },
    onSuccess: (data) => {
      setIsSubmitted(true);
      toast({
        title: t('reviewForm.success.title'),
        description: data.message || t('reviewForm.success.description'),
      });
      queryClient.invalidateQueries({ queryKey: ["/api/reviews"] });
      queryClient.invalidateQueries({ queryKey: ["/api/reviews/approved"] });
      onSuccess?.();
    },
    onError: (error: any) => {
      toast({
        title: t('reviewForm.error.title'),
        description: error.message || t('reviewForm.error.description'),
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: ReviewFormData) => {
    createReview.mutate(data);
  };

  const renderStarRating = () => {
    const rating = parseInt(form.watch("rating")) || 0;
    return (
      <div className="flex gap-1" data-testid="star-rating">
        {[1, 2, 3, 4, 5].map((star) => (
          <button
            key={star}
            type="button"
            onClick={() => form.setValue("rating", star.toString())}
            className={`text-2xl ${
              star <= rating 
                ? "text-yellow-400" 
                : "text-gray-300 dark:text-gray-600"
            } hover:text-yellow-300 transition-colors`}
            data-testid={`star-${star}`}
          >
            <Star fill="currentColor" />
          </button>
        ))}
      </div>
    );
  };

  if (isSubmitted) {
    return (
      <Card className="bg-green-50 dark:bg-green-900/20 border-green-200 dark:border-green-800">
        <CardContent className="pt-6">
          <div className="text-center">
            <CheckCircle className="w-16 h-16 text-green-600 dark:text-green-400 mx-auto mb-4" />
            <h3 className="text-lg font-semibold text-green-800 dark:text-green-200 mb-2">
              {t('reviewForm.success.title')}
            </h3>
            <p className="text-green-700 dark:text-green-300 mb-4">
              {t('reviewForm.success.message')}
            </p>
            <Button 
              onClick={() => setIsSubmitted(false)}
              variant="outline"
              className="text-green-700 dark:text-green-300 border-green-300 dark:border-green-600"
              data-testid="button-submit-another"
            >
              {t('reviewForm.success.submitAnother')}
            </Button>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="bg-white dark:bg-gray-800">
      <CardHeader>
        <CardTitle className="text-2xl font-bold text-blue-900 dark:text-blue-100">
          {t('reviewForm.title')}
        </CardTitle>
        <CardDescription>
          {t('reviewForm.description')}
        </CardDescription>
      </CardHeader>
      <CardContent>
        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <FormField
                control={form.control}
                name="customerName"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>{t('reviewForm.form.fullName')}</FormLabel>
                    <FormControl>
                      <Input placeholder={t('reviewForm.form.fullNamePlaceholder')} {...field} data-testid="input-name" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="customerEmail"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>{t('reviewForm.form.emailAddress')}</FormLabel>
                    <FormControl>
                      <Input type="email" placeholder={t('reviewForm.form.emailPlaceholder')} {...field} data-testid="input-email" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="customerPhone"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>{t('reviewForm.form.phoneNumber')}</FormLabel>
                  <FormControl>
                    <Input placeholder={t('reviewForm.form.phonePlaceholder')} {...field} value={field.value || ""} data-testid="input-phone" />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <FormField
                control={form.control}
                name="serviceUsed"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>{t('reviewForm.form.serviceUsed')}</FormLabel>
                    <Select onValueChange={field.onChange} defaultValue={field.value}>
                      <FormControl>
